export default {
  methods: {
    initData() {
      const echarts = require('echarts/lib/echarts');
      // Introduce pie chart component
      require('echarts/lib/chart/pie');
      require('echarts/lib/component/tooltip');
      // Based on the prepared dom, initialize the echarts instance
      const myChart = echarts.init(document.getElementById('main1'));
      myChart.setOption({
        tooltip: {
          trigger: 'item',
          formatter: '{a} <br/>{b}: {c} ({d}%)' // See the comments below for the specific attributes represented by a b c d
        },

        series: [{
          name: 'Access source',
          // a in formatter
          type: 'pie',
          radius: '6%',
          center: ['50%', '60%'],
          data: [{
            value: 12,
            name: 'За',
            itemStyle: {
              color: '#FFFF00'
            }
          }, {
            value: 18,
            name: 'Против',
            itemStyle: {
              color: '#008000'
            }
          }],
          // The name in the data is b in the formatter; the value in the data is the c in the formatter; as for d is the percentage calculated by Echarts; itemStyle: the color of each sector of the pie chart; label is behind the indicator line The style of the text, labelLine is the color of the indicator line
          label: {
            normal: {
              show: true,
              textStyle: {
                fontWeight: 400,
                fontSize: 18 // font size of the text
              },

              formatter: '{b} \n {c} ' // This is the prompt message behind the indicator line. The line break here is different from the formatter in the tooltips above
            }
          },

          itemStyle: {
            emphasis: {
              shadowBlur: 10,
              shadowOffsetX: 0,
              shadowColor: 'rgba(0, 0, 0, 0.5)'
            }
          }
        }]
      });
      window.onresize = myChart.resize; //Adapt the size of the browser window
    }
  },

  mounted() {
    this.initData();
  }
};
