import { createStore } from 'vuex'
import axios from 'axios'

export default createStore({
  state: {
    posts: [],
    events: [
      {
        id: 1,
        name: 'Ебатория 1',
        date: '24.08.2067',
        voteFor: 19,
        voteAgaints: 11,
      }
    ]
  },
  getters: {
    POSTS(state) {
      return state.posts
    }
  },
  mutations: {
    SET_POSTS_TO_STATE(state, posts) {
      state.posts = posts;
    }
  },
  actions: {
    // async GET_POSTS_FROM_API({commit}, posts) {
    //   try {
    //     const response = await axios.get('https://jsonplaceholder.typicode.com/posts?_limit=10');
    //     posts = response.data
    //     commit('SET_POSTS_TO_STATE', posts)
    //   } catch(er) {
    //     alert(er)
    //   }
    // },
    GET_POSTS_FROM_API({commit}) {
      return axios
        .get("https://jsonplaceholder.typicode.com/posts?_limit=10")
        .then(r => r.data)
        .then(posts => {
          commit('SET_POSTS_TO_STATE', posts)
        })

        .catch((error) => {
          console.log(error)
          return error;
        })

    }
  },
  modules: {
  }
})
