import { createStore } from 'vuex'
import axios from 'axios'

export default createStore({
  state: {
    posts: [],
    events: [
      {
        id: 1,
        title: 'Ебатория 1',
        name: 'Коломоец Александра Вячеславовна',
        description: 'Для тайного голосавания по присуждению ученой степени кандидата геолого-минералогичексих наук<br/>Диссертационный совет <strong>24.2.354.02</strong><br/>при ФГБОУ ВО «Российский государственный геологоразведочны университет имени Серго Орджоникидзе»<br/>К заседанию диссертационного совета № 01/10/2022 от<br/> «14» октября 2022 г.',
        date: '24.08.2067',
        total: 30,
        voteFor: 19,
        voteAgainst: 11,
      },
      {
        id: 2,
        title: 'Ебатория 2',
        name: 'Нечипоренко Алексей Артёмович',
        description: 'Для тайного голосавания по присуждению ученой степени кандидата геолого-минералогичексих наук<br/>Диссертационный совет <strong>24.2.354.02</strong><br/>при ФГБОУ ВО «Российский государственный геологоразведочны университет имени Серго Орджоникидзе»<br/>К заседанию диссертационного совета № 01/10/2022 от<br/> «14» октября 2022 г.',
        date: '24.08.2067',
        total: 30,
        voteFor: 5,
        voteAgainst: 15,
      }
    ]
  },
  getters: {
    POSTS(state) {
      return state.posts
    },

    EVENTS(state) {
      return state.events
    }
  },
  mutations: {
    SET_POSTS_TO_STATE(state, posts) {
      state.posts = posts;
    }
  },
  actions: {
    //  async GET_POSTS_FROM_API({commit}, posts) {
    //    try {
    //      const response = await axios.get('https://voiting-server.cfapps.us10-001.hana.ondemand.com/test/get-data');
    //      posts = response.data
    //      commit('SET_POSTS_TO_STATE', posts)
    //    } catch(er) {
    //      alert(er)
    //    }
    //  },
     GET_POSTS_FROM_API({commit}) {
       return axios
         .get("https://voiting-server.cfapps.us10-001.hana.ondemand.com/test/get-data")
         .then(r => r.data)
         .then(posts => {
           commit('SET_POSTS_TO_STATE', posts)
         })

         .catch((error) => {
           console.log(error)
           return error;
         })

     }
  },
  modules: {
  }
})
