import { createStore } from 'vuex'
import axios from 'axios'

export default createStore({
  state: {
    posts: [],
    events: [
      {
        id: 1,
        name: 'Ебатория 1',
        date: '24.08.2067',
        voteFor: 19,
        voteAgainst: 11,
      },
      {
        id: 2,
        name: 'Ебатория 2',
        date: '24.08.2067',
        voteFor: 5,
        voteAgainst: 15,
      }
    ]
  },
  getters: {
    POSTS(state) {
      return state.posts
    },

    EVENTS(state) {
      return state.events
    }
  },
  mutations: {
    SET_POSTS_TO_STATE(state, posts) {
      state.posts = posts;
    }
  },
  actions: {
    // async GET_POSTS_FROM_API({commit}, posts) {
    //   try {
    //     const response = await axios.get('https://jsonplaceholder.typicode.com/posts?_limit=10');
    //     posts = response.data
    //     commit('SET_POSTS_TO_STATE', posts)
    //   } catch(er) {
    //     alert(er)
    //   }
    // },
    GET_POSTS_FROM_API({commit}) {
      return axios
        .get("https://voiting-server.cfapps.us10-001.hana.ondemand.com/test/get-data")
        .then(r => r.data)
        .then(posts => {
          commit('SET_POSTS_TO_STATE', posts)
        })

        .catch((error) => {
          console.log(error)
          return error;
        })

    }
  },
  modules: {
  }
})
