      //   ]
      // }
    ]
  },
  getters: {
    POSTS(state) {
      return state.posts
    },

    EVENTS(state) {
      return state.events
    }
  },
  mutations: {
    SET_POSTS_TO_STATE(state, posts) {
      state.posts = posts;
      console.log(posts)
    },

    PUT_EVENT_TO_STATE(state, newEvent) {
      state.events[newEvent.id] = newEvent;
    },

    SET_EVENTS_TO_STATE(state, events) {
      state.events = events;
    }
  },
  actions: {
    //  async GET_POSTS_FROM_API({commit}, posts) {
    //    try {
