    //      posts = response.data
    //      commit('SET_POSTS_TO_STATE', posts)
    //    } catch(er) {
    //      alert(er)
    //    }
    //  },
    GET_POSTS_FROM_API({commit}) {
      return axios
        .get("https://voiting-server.cfapps.us10-001.hana.ondemand.com/test/get-data")
        .then(r => r.data)
        .then(posts => {
          commit('SET_POSTS_TO_STATE', posts)
        })

        .catch((error) => {
          console.log(error)
          return error;
        })
    },

    GET_EVENTS_FROM_API({commit}) {
      return axios
        .get("https://voiting-server.cfapps.us10-001.hana.ondemand.com/api/event/get-all")
        .then(r => r.data)
        .then(events => {
          commit('SET_EVENTS_TO_STATE', events)
        })

        .catch((error) => {
          console.log(error)
          return error;
        })
    },

    CREATE_EVENT_BY_API({commit}) {
      return axios
      .get("https://voiting-server.cfapps.us10-001.hana.ondemand.com/api/voiting/create")
      .then(r => r.data)
      .then(posts => {
        commit('SET_POSTS_TO_STATE', posts)
      })

      .catch((error) => {
        console.log(error)
        return error;
      })
    },

    PUT_EVENT_FROM_API({commit}, newEvent) {
      commit('PUT_EVENT_TO_STATE', newEvent)
    }

  },
  modules: {

  }
})
