import { AgChartsVue } from 'ag-charts-vue3';
import { mapGetters } from 'vuex';
export default {
  components: {
    'ag-charts-vue': AgChartsVue
  },
  data: function () {
    return {
      options: null,
      event: {}
    };
  },
  methods: {
    createChart() {
      this.options = {
        data: [{
          label: 'За',
          value: this.event.voteFor
        }, {
          label: 'Против',
          value: this.event.voteAgainst
        }],
        series: [{
          type: 'pie',
          angleKey: 'value',
          calloutLabelKey: 'label',
          sectorLabelKey: 'value',
          sectorLabel: {
            color: 'white',
            fontWeight: 'bold'
          }
        }]
      };
    },
    getEvent() {
      this.event = this.EVENTS[this.$route.params['id'] - 1];
    }
  },
  created() {
    this.createChart();
  },
  mounted() {
    this.getEvent();
    this.createChart();
  },
  computed: {
    ...mapGetters(['EVENTS'])
  }
};
